        function AmountConversionByCurrencyRate(amount, rate, type) ");
                WriteLiteral(@"{
            let returnAmount = 0;
            if (type == 0) {
                if (rate != 0)
                    returnAmount = amount / rate;
                else
                    returnAmount = amount;
            } else {
                if (rate != 0)
                    returnAmount = amount * rate;
                else
                    returnAmount = amount;
            }
            return RtnFloatUpToPrecision(returnAmount, 4)
        }
       
      

